import React, { useState, useEffect } from "react";
import Axios from "axios";
import Select from "react-select";
import { Header } from "./PagesElements";
import "./PagesElements.css";

const PaperVersion = () => {
  const [prescriptionDocument, setPrescriptionDocument] = useState([]);
  const [prescriptionPaperVersion, setPrescriptionPaperVersion] = useState([]);

  const getDocument = (selectedOption) => {
    console.log(selectedOption);
    Axios.post("http://localhost:3001/api/add/document/" + { selectedOption }, {
      selectedOption: selectedOption,
    }).then((res) => {
      //   console.log(res.data);
    //   setPrescriptionDocument(res.data);
      getPaperVersion(res.data);
    });
  };

  const getPaperVersion = (data) => {
    console.log(prescriptionDocument);
    Axios.post("http://localhost:8080/api/test4", {
      prescriptionDocument: prescriptionDocument,
    }).then((res) => {
      setPrescriptionPaperVersion(data);
      console.log(data);
      document.getElementById("log").innerHTML = prescriptionPaperVersion;
    });
  };
  useEffect(() => {
    getDocument("recepta_wersja_papierowa.xml");
    getPaperVersion();
    setTimeout(() => {
      getPaperVersion();
      getPaperVersion();
    }, 10000);
  }, []);
  useEffect(() => {
    getPaperVersion();
    setTimeout(() => {
      getPaperVersion();
    }, 1000);
  }, []);

  return (
    <div className="App">
      <button onClick={getPaperVersion}>Pokaż Receptę</button>
      <div className="paperVersion" id="log"></div>
    </div>
  );
};

export default PaperVersion;
