import React, { useState, useEffect } from "react";
import Axios from "axios";
import Select from "react-select";
import { Header } from "./PagesElements";
import "./PagesElements.css";

const Add = () => {
  const [id_recepty, setIdRecepty] = useState("");
  const [klucz_recepty, setKluczRecepty] = useState("");
  const [id_pakietu_recept, setIdPakietuRecept] = useState("");
  const [id_lekarstwa, setIdLekarstwa] = useState("");
  const [id_użytkownika, setIdUżytkownika] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [selectedOption, setSelectedOption] = useState("");
  const [medicineInformations, setMedicineInformations] = useState([]);
  const [prescriptions, setPrescriptions] = useState([]);
  const [prescriptionPackage, setPrescriptionPackage] = useState([]);

  const submitStatus = () => {
    Axios.post("http://localhost:3001/api/add", {
      id_recepty: id_recepty,
      klucz_recepty: klucz_recepty,
      id_pakietu_recept: id_pakietu_recept,
      id_lekarstwa: id_lekarstwa,
      id_użytkownika: id_użytkownika,
    }).then(() => {
      alert("succesful insert");
      submitPackage(id_pakietu_recept);
    });
  };

  const javaTest = () => {
    Axios.get("http://localhost:8080/api/test", {}).then((res) => {
      console.log(res.data);
    });
  };

  const getPrescriptionSearchData = () => {
    Axios.post("http://localhost:8080/api/test3", {}).then((res) => {
      console.log(res.data);
    });
  };

  const submitMedicine = (selectedOption) => {
    console.log(selectedOption);
    Axios.post("http://localhost:3001/api/add/" + { selectedOption }, {
      id: selectedOption,
    }).then((res) => {
      setIdLekarstwa(res.data[0].id_lekarstwa);
      setMedicineInformations(res.data);
    });
  };

  const getPrescriptionPackage = () => {
    Axios.post("http://localhost:3001/api/add/prescription", {}).then((res) => {
      console.log(res.data);
      setPrescriptions(res.data);
    });
  };
  useEffect(() => {
    getMedicines();
    setTimeout(() => {
      submitMedicine(1);
    }, 1000);
  }, []);
  const getMedicines = () => {
    Axios.get("http://localhost:3001/api/add", {}).then((res) => {
      setMedicines(res.data);
    });
  };

  const getPackages = () => {
    Axios.get("http://localhost:3001/api/add/package", {}).then((res) => {
      console.log(res.data);
      setPrescriptions(res.data);
    });
  };

  const submitPackage = (selectedOption) => {
    Axios.post("http://localhost:3001/api/add/package/" + { selectedOption }, {
      id: selectedOption,
    }).then((res) => {
      console.log(res.data);
      setPrescriptionPackage(res.data);
    });
  };

  let MedicineList = medicines.map((medicine, i) => {
    return (
      <option key={i} value={medicine.id_lekarstwa}>
        {medicine.nazwa}
      </option>
    );
  }, this);

  let PrescriptionList = prescriptions.map((prescription, i) => {
    return (
      <option key={i} value={prescription.id_pakietu_recept}>
        {prescription.id_pakietu_recept}
      </option>
    );
  }, this);

  return (
    <div className="App">
      <Header>Dodaj Receptę</Header>
      <div className="inputsWrapper">
        <div>
          <label>ID Recepty:</label>
          <input
            type="text"
            name="id_recepty"
            onChange={(e) => {
              setIdRecepty(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Klucz Recepty:</label>
          <input
            type="text"
            name="klucz_recepty"
            onChange={(e) => {
              setKluczRecepty(e.target.value);
            }}
          />
        </div>
        <div>
          <label>ID Pakietu Recept:</label>
          <input
            type="text"
            name="id_pakietu_recept"
            onChange={(e) => {
              setIdPakietuRecept(e.target.value);
            }}
          />
        </div>
        <div>
          <label>ID Użytkownika:</label>
          <input
            type="text"
            name="id_użytkownika"
            onChange={(e) => {
              setIdUżytkownika(e.target.value);
            }}
          />
        </div>
      </div>

      <button onClick={submitStatus}>submitStatus</button>
      <button onClick={javaTest}>javaTest</button>
      <button onClick={getMedicines}>getMedicines</button>
      <button onClick={getPackages}>getPackages</button>

      <select
        value
        onChange={(e) => {
          console.log()
          submitMedicine(e.target.value);
        }}
      >
        {MedicineList}
      </select>

      <select
        value={null}
        onChange={(e) => {
          submitPackage(e.target.value);
        }}
      >
        {PrescriptionList}
      </select>
      {/* <table className="styled-table"> */}
      {/* <div className="scrollit"> */}
      {/* <thead>
                      <tr>
                          <th>EAN</th>
                          <th>jednostka_dawki</th>
                          <th>dawka</th>
                          <th>substancja_czynna</th>
                          <th>nazwa</th>
                          <th>postac</th>
                          <th>zawartosc_opakowania</th>
                          <th>rodzaj_leku</th>
                          <th>urzedowa_cena_zbytu</th>
                          <th>cena_hurtowa_brutto</th>
                          <th>cena_detaliczna</th>
                          <th>wysokosc_limitu_finansowania</th>
                          <th>doplata_swiadczeniobiorcy</th>
                          <th>poziom_odplatnosci</th>
                          <th>lista_refundacyjna</th>
                          <th>wskazania</th>
                      </tr>
                  </thead> */}
      {/* <tbody> */}
      {console.log(medicineInformations)}
      {medicineInformations &&
        medicineInformations.map((information) => (
          <div className="medicineTable">
            <div>
              <p>{information.EAN}</p> <p>EAN</p>
            </div>
            <div>
              <p>{information.EAN}</p> <p>EAN</p>
            </div>
            <div>
              <p>{information.EAN}</p> <p>EAN</p>
            </div>
            <div>
