import React, { useState, useEffect } from "react";
import Axios from "axios";
import Select from "react-select";
import { Header } from "./PagesElements";
import "./PagesElements.css";
import Login from "./Login.js";

const Add = () => {
  // Hook useState zwracający tablicę z dwoma elementami
  const [id_pakietu_recept, setIdPakietuRecept] = useState("");
  const [id_lekarstwa, setIdLekarstwa] = useState("");
  const [id_uslugobiorcy, setIdUslugobiorcy] = useState("");

  const [patientName, SetPatientName] = useState("");
  const [patientSurname, SetPatientSurname] = useState("");
  const [patientPesel, SetPatientPesel] = useState("");
  const [patientCity, SetPatientCity] = useState("");
  const [patientStreet, SetPatientStreet] = useState("");
  const [patientHouseNumber, SetPatientHouseNumber] = useState("");
  const [patientGender, SetPatientGender] = useState("");
  const [patientDateOfBirth, SetPatientDateOfBirth] = useState("");

  const [usageDescription, setUsageDescription] = useState("");

  const [doctorId, setDoctorId] = useState("");

  const [ilosc_leku, setIloscLeku] = useState("");
  const [medicines, setMedicines] = useState([]);
  const [userInformations, setUserInformations] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedOption, setSelectedOption] = useState("");
  const [medicineInformations, setMedicineInformations] = useState([]);
  const [prescriptionDocument, setPrescriptionDocument] = useState([]);
  const [prescriptionPaperVersion, setPrescriptionPaperVersion] = useState([]);

  const submitStatus = () => {
    setIdLekarza(e.target.value);
    Axios.post("http://localhost:3001/api/add", {
      id_pakietu_recept: id_pakietu_recept,
      stosowanie: usageDescription,
      id_lekarstwa: id_lekarstwa,
      id_uslugobiorcy: id_uslugobiorcy,
      id_lekarza: doctorId,
      ilosc_leku: ilosc_leku,
    }).then((res) => {
      alert("succesful insert");
    });
  };

  const addUser = () => {
    Axios.post("http://localhost:3001/api/user", {
      imie: patientName,
      nazwisko: patientSurname,
      pesel: patientPesel,
      miasto: patientCity,
      ulica: patientStreet,
      numer_domu: patientHouseNumber,
      plec: patientGender,
      data_urodzenia: patientDateOfBirth,
    }).then((res) => {
      console.log(res);
      getUsers();
    });
  };

  const javaTest = () => {
    getDocument("recepta_wypelniona_podpisana.xml");
    console.log(prescriptionDocument);
    Axios.post("http://localhost:8080/api/test", {
      prescriptionDocument: prescriptionDocument,
    }).then((res) => {
      alert(res.data);
    });
  };

  const getPaperVersion = () => {
    getDocument("recepta_wersja_papierowa.xml");
    Axios.post("http://localhost:8080/api/test4", {
      prescriptionDocument: prescriptionDocument,
    }).then((res) => {
      setPrescriptionPaperVersion(res.data);
      document.getElementById("log").innerHTML += prescriptionPaperVersion;
    });
  };

  const anulowanieTest = () => {
    Axios.get("http://localhost:8080/api/test2", {}).then((res) => {
      console.log(res.data);
    });
  };

  const getPrescriptionSearchData = () => {
    Axios.post("http://localhost:8080/api/test3", {}).then((res) => {
      console.log(res.data);
    });
  };

  const submitMedicine = (selectedOption) => {
    console.log(selectedOption);
    Axios.post("http://localhost:3001/api/add/" + { selectedOption }, {
      id: selectedOption,
    }).then((res) => {
      setIdLekarstwa(res.data[0].id_lekarstwa);
      console.log(res.data);
      setMedicineInformations(res.data);
    });
  };
  useEffect(() => {
    getMedicines();
    getUsers();
    setTimeout(() => {
      submitMedicine(1);
      submitUser(1);
    }, 1000);
  }, []);
  const getMedicines = () => {
    Axios.get("http://localhost:3001/api/add", {}).then((res) => {
      setMedicines(res.data);
    });
  };

  const getUsers = () => {
    Axios.get("http://localhost:3001/api/user", {}).then((res) => {
      console.log(res.data);
      setUsers(res.data);
    });
  };

  const submitUser = (selectedOption) => {
    Axios.post("http://localhost:3001/api/user/" + { selectedOption }, {
      id: selectedOption,
    }).then((res) => {
      console.log(res.data);
      setUserInformations(res.data);
      setIdUslugobiorcy(res.data[0].id_użytkownika);
    });
  };

  const getDocument = (selectedOption) => {
    console.log(selectedOption);
    Axios.post("http://localhost:3001/api/add/document/" + { selectedOption }, {
      selectedOption: selectedOption,
    }).then((res) => {
      setPrescriptionDocument(res.data);
    });
  };

  let MedicineList = medicines.map((medicine, i) => {
    return (
      <option key={i} value={medicine.id_lekarstwa}>
        {medicine.nazwa}
      </option>
    );
  }, this);

  let UserList = users.map((user, i) => {
    return (
      <option key={i} value={user.id_użytkownika}>
        {user.nazwisko}
      </option>
    );
  }, this);

  return (
    <div className="App">
      <Header>Dodaj Receptę</Header>
      <div className="user_input_table">
        <div>
          <label>ID Pakietu Recept:</label>
          <input
            type="text"
            name="id_pakietu_recept"
            onChange={(e) => {
              // Wywołanie funkcji zmieniającej stan, wraz z wpisaną wartością przez użytkownika
              setIdPakietuRecept(e.target.value);
            }}
          />
        </div>
        <div class="form__group field">
          <input
            placeholder="ID Lekarza"
            required
            class="form__field"
            type="text"
            name="doctors_id"
            onChange={(e) => {
              setIdLekarza(e.target.value);
            }}
          />
          <label class="form__label">ID Lekarza</label>
        </div>
        <div class="form__group field">
          <input
            placeholder="ID Lekarza"
            required
            class="form__field"
            type="text"
            name="usage_description"
            onChange={(e) => {
              setUsageDescription(e.target.value);
            }}
          />
          <label class="form__label">Opis stosowania</label>
        </div>
        <div class="form__group field">
          <input
            placeholder="ID Lekarza"
            required
            class="form__field"
            type="text"
            name="ilosc_leku"
            onChange={(e) => {
              setIloscLeku(e.target.value);
            }}
          />
          <label class="form__label">Ilość leku</label>
        </div>
      </div>
      <div className="paperVersion" id="log"></div>
      <button onClick={submitStatus}>submitStatus</button>
      <button onClick={addUser}>addUser</button>
      <button onClick={javaTest}>javaTest</button>
      <button onClick={anulowanieTest}>anulowanieTest</button>
      <button onClick={getMedicines}>getMedicines</button>
      <button onClick={getUsers}>getUsers</button>
      <button onClick={getPaperVersion}>getPaperVersion</button>
      <div class="user_input_table">
        <div>
          <label>Imię</label>
          <input
            type="text"
            name="patient_name"
            onChange={(e) => {
              SetPatientName(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Nazwisko</label>
          <input
            type="text"
            name="patient_surname"
            onChange={(e) => {
              SetPatientSurname(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Pesel</label>
          <input
            type="text"
            name="patient_pesel"
            onChange={(e) => {
              SetPatientPesel(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Miasto zamieszkania</label>
          <input
            type="text"
            name="patient_city"
            onChange={(e) => {
              SetPatientCity(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Ulica zamieszkania</label>
          <input
            type="text"
            name="patient_street"
            onChange={(e) => {
              SetPatientStreet(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Numer domu</label>
          <input
            type="text"
            name="patient_house_number"
            onChange={(e) => {
              SetPatientHouseNumber(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Płeć</label>
          <input
            type="text"
            name="patient_gender"
            onChange={(e) => {
              SetPatientGender(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Data Urodzenia</label>
          <input
            type="date"
            name="patient_date_of_birth"
            onChange={(e) => {
              SetPatientDateOfBirth(e.target.value);
            }}
          />
        </div>
      </div>
      {/* <table className="styled-table"> */}
      {/* <div className="scrollit"> */}
      {/* <thead>
                      <tr>
                          <th>EAN</th>
                          <th>jednostka_dawki</th>
                          <th>dawka</th>
                          <th>substancja_czynna</th>
                          <th>nazwa</th>
                          <th>postac</th>
                          <th>zawartosc_opakowania</th>
                          <th>rodzaj_leku</th>
                          <th>urzedowa_cena_zbytu</th>
                          <th>cena_hurtowa_brutto</th>
                          <th>cena_detaliczna</th>
                          <th>wysokosc_limitu_finansowania</th>
                          <th>doplata_swiadczeniobiorcy</th>
                          <th>poziom_odplatnosci</th>
                          <th>lista_refundacyjna</th>
                          <th>wskazania</th>
                      </tr>
                  </thead> */}
      {/* <tbody> */}
      <div class="medicine_select">
        <select
          value={null}
          onChange={(e) => {
            submitMedicine(e.target.value);
          }}
        >
          {MedicineList}
        </select>
      </div>
      <div className="user_select">
        <select
          value={null}
          onChange={(e) => {
            submitUser(e.target.value);
          }}
        >
          {UserList}
        </select>
      </div>
      {console.log(medicineInformations)}
      {medicineInformations &&
        medicineInformations.map((information) => (
          // <div className="medicineTable">
          //   <div>
          //     <p>{information.nazwa}</p> <p>Nazwa lekarstwa</p>
          //   </div>
          //   <div>
          //     <p>{information.EAN}</p> <p>numer EAN</p>
          //   </div>
          //   <div>
          //     <p>{information.postac}</p> <p>Postać</p>
          //   </div>
          //   <div>
          //     <p>
          //       {information.dawka}
          //       {information.jednostka_dawki}
          //     </p>{" "}
          //     <p>Dawka</p>
          //   </div>
          //   <div>
          //     <p>{information.cena_detaliczna}</p> <p>Cena</p>
          //   </div>
          //   <div>
          //     <p>{information.substancja_czynna}</p> <p>Substancja czynna</p>
          //   </div>
          //   <div>
          //     <p>{information.zawartosc_opakowania}</p>{" "}
          //     <p>Zawartość opakowania</p>
          //   </div>
          // </div>
          <div class="st_wrap_table" data-table_id="1">
            <header class="st_table_header">
              <h1>Lekarstwa</h1>
            </header>
            <div class="st_table">
              <div class="st_row">
                <div class="st_column _name">Nazwa lekarstwa</div>
                <div class="st_column _value">{information.nazwa}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">numer EAN</div>
                <div class="st_column _value">{information.EAN}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Postać</div>
                <div class="st_column _value">{information.postac}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Dawka</div>
                <div class="st_column _value">
                  {information.dawka}
                  {information.jednostka_dawki}
                </div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Cena</div>
                <div class="st_column _value">
                  {information.cena_detaliczna}
                </div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Substancja czynna</div>
                <div class="st_column _value">
                  {information.substancja_czynna}
                </div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Zawartość opakowania</div>
                <div class="st_column _value">
                  {information.zawartosc_opakowania}
                </div>
              </div>
            </div>
          </div>
        ))}
      {userInformations &&
        userInformations.map((information) => (
          // <div className="medicineTable">
          //   <div>
          //     <p>{information.imie}</p> <p>Nazwa lekarstwa</p>
          //   </div>
          //   <div>
          //     <p>{information.nazwisko}</p> <p>numer EAN</p>
          //   </div>
          //   <div>
          //     <p>{information.plec}</p> <p>Postać</p>
          //   </div>
          //   <div></div>
          // </div>
          <div class="st_wrap_table" data-table_id="2">
            <header class="st_table_header">
              <h1>Pacjenci</h1>
            </header>
            <div class="st_table">
              <div class="st_row">
                <div class="st_column _name">Imię</div>
                <div class="st_column _value">{information.imie}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Nazwisko</div>
                <div class="st_column _value">{information.nazwisko}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Pesel</div>
                <div class="st_column _value">{information.pesel}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Płeć</div>
                <div class="st_column _value">{information.plec}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Data urodzenia</div>
                <div class="st_column _value">{information.data_urodzenia}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Miasto</div>
                <div class="st_column _value">{information.miasto}</div>
              </div>
              <div class="st_row">
                <div class="st_column _name">Ulica</div>
                <div class="st_column _value">
                  {information.ulica} <span></span>
                  {information.numer_domu}
                </div>
              </div>
            </div>
          </div>
        ))}
      {/* <td>{information.EAN} <p>EAN</p></td>
                              <td>{information.jednostka_dawki}<p>EANasdasd</p></td>
                              <td>{information.dawka}<p>EANasdas asdaddassdsd</p></td> */}
      {/* <td>{information.substancja_czynna}</td>
                              <td>{information.nazwa}</td>
